var validate=function(data){var form=$('form[id$='+data.formName+']');var success=!0;if(data.fields.length>0){for(var i=0;i<data.fields.length;i++){var currentField=data.fields[i];var field=form.find('input[id$='+currentField.name+']');field.parent().find('.error').remove();if(!currentField.isValid){var message=(currentField.error!=undefined)?currentField.error:'Invalid '+currentField.label;field.parent().append('<p class="error">'+message+'</p>');success=!1}}}return success}